// src/components/Facturar.jsx
import React, { useState } from "react";
import { verificarAntecedentes } from "../services/api";

const Facturar = () => {
  const [cedula, setCedula] = useState("");
  const [respuesta, setRespuesta] = useState(null);
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setRespuesta(null);
    setError("");

    try {
      const token = localStorage.getItem("token"); // O como sea que guardes tu JWT
      const data = await verificarAntecedentes(cedula, token);
      setRespuesta(data.estado);
    } catch (err) {
      setError(err.message || "No se pudo conectar con el servicio");
    }
  };

  return (
    <div>
      <h2>Facturar</h2>
      <form onSubmit={handleSubmit}>
        <label>
          CÃ©dula del cliente:
          <input
            type="text"
            value={cedula}
            onChange={(e) => setCedula(e.target.value)}
            required
          />
        </label>
        <button type="submit">Validar antecedentes</button>
      </form>

      {respuesta && <p>Resultado: {respuesta}</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}
    </div>
  );
};

export default Facturar;
