// src/components/SubirInventario.jsx
import React, { useState } from "react";
import { addInventario } from "../services/api"; 

const SubirInventario = () => {
  const [formData, setFormData] = useState({
    nombre: "",
    categoria: "",
    cantidad: 0,
    estado: "",
    descripcion: "",
    ubicacion: ""
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: name === "cantidad" ? parseInt(value) : value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token"); // o de donde obtengas tu token

    try {
      const respuesta = await addInventario(token, formData);
      alert("Producto agregado exitosamente");
      console.log(respuesta);
      setFormData({
        nombre: "",
        categoria: "",
        cantidad: 0,
        estado: "",
        descripcion: "",
        ubicacion: ""
      });
    } catch (error) {
      console.error("Error al subir inventario:", error.message);
      alert("Error al agregar producto");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input name="nombre" placeholder="Nombre" value={formData.nombre} onChange={handleChange} required />
      <input name="categoria" placeholder="Categoría" value={formData.categoria} onChange={handleChange} required />
      <input name="cantidad" type="number" placeholder="Cantidad" value={formData.cantidad} onChange={handleChange} required />
      <input name="estado" placeholder="Estado" value={formData.estado} onChange={handleChange} required />
      <input name="descripcion" placeholder="Descripción" value={formData.descripcion} onChange={handleChange} />
      <input name="ubicacion" placeholder="Ubicación" value={formData.ubicacion} onChange={handleChange} />

      <button type="submit">Agregar al Inventario</button>
    </form>
  );
};

export default SubirInventario;
