import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate, Link } from 'react-router-dom';

const API_URL = 'http://localhost:4000';

function Login({ onLogin }) {
  const [usuario, setUsuario] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async e => {
    e.preventDefault();
    setError('');
    try {
      const res = await fetch(`${API_URL}/login`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ usuario, password }),
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data.message || 'Error');
      localStorage.setItem('token', data.token);
      onLogin(data.user);
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div style={{ maxWidth: 400, margin: 'auto' }}>
      <h2>Login</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <form onSubmit={handleSubmit}>
        <label>Usuario:</label>
        <input value={usuario} onChange={e => setUsuario(e.target.value)} required />
        <label>Contrase침a:</label>
        <input type="password" value={password} onChange={e => setPassword(e.target.value)} required />
        <button type="submit">Conectar</button>
      </form>
    </div>
  );
}

function Dashboard({ user, onLogout }) {
  const [message, setMessage] = useState('');

  useEffect(() => {
    const fetchDashboard = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await fetch(`${API_URL}/dashboard`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        const data = await res.json();
        if (!res.ok) throw new Error(data.message || 'Error');
        setMessage(data.message);
      } catch (err) {
        onLogout();
      }
    };
    fetchDashboard();
  }, [onLogout]);

  return (
    <div style={{ maxWidth: 600, margin: 'auto' }}>
      <h2>Dashboard</h2>
      <p>{message}</p>
      <p>Usuario: {user.nombre}</p>
      <p>Email: {user.email}</p>
      <p>Rango: {user.rango}</p>
      <button onClick={onLogout}>Cerrar sesi칩n</button>
    </div>
  );
}

function App() {
  const [user, setUser] = useState(null);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
  };

  // Al cargar la app verifico si hay token y user guardado para mantener sesi칩n
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      // Decodificar token para obtener datos (sin validar)
      const payload = JSON.parse(atob(token.split('.')[1]));
      setUser({
        nombre: payload.nombre,
        email: payload.email,
        rango: payload.permiso,
      });
    }
  }, []);

  return (
    <Router>
      <Routes>
        <Route path="/" element={user ? <Navigate to="/dashboard" /> : <Login onLogin={setUser} />} />
        <Route path="/dashboard" element={user ? <Dashboard user={user} onLogout={handleLogout} /> : <Navigate to="/" />} />
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </Router>
  );
}

export default App;
