import React, { useEffect, useState } from "react";
import { verificarAntecedentes, getInventario, facturarCliente } from "../services/api";

const Facturar = () => {
  const [cedula, setCedula] = useState("");
  const [respuesta, setRespuesta] = useState(null);
  const [error, setError] = useState("");
  const [inventario, setInventario] = useState([]);
  const [productosSeleccionados, setProductosSeleccionados] = useState([]);

  const token = localStorage.getItem("token");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setRespuesta(null);
    setError("");
    setProductosSeleccionados([]);

    try {
      const data = await verificarAntecedentes(cedula, token);
      setRespuesta(data.estado);

    if (data.estado?.toLowerCase() === "sin antecedentes") {
      const productos = await getInventario(token);
      setInventario(productos);
    }
    } catch (err) {
      setError(err.message || "No se pudo conectar con el servicio");
    }
  };

  const handleAgregarProducto = (producto) => {
    const existente = productosSeleccionados.find((p) => p._id === producto._id);
    if (existente) {
      setProductosSeleccionados((prev) =>
        prev.map((p) =>
          p._id === producto._id && p.cantidad < producto.cantidad
            ? { ...p, cantidad: p.cantidad + 1 }
            : p
        )
      );
    } else {
      setProductosSeleccionados((prev) => [...prev, { ...producto, cantidad: 1 }]);
    }
  };

  const handleFacturar = async () => {
    try {
      const factura = {
        cedula,
        productos: productosSeleccionados.map(({ _id, cantidad }) => ({ id: _id, cantidad }))
      };

      const result = await facturarCliente(factura, token);
      alert("Factura generada con éxito");
      setProductosSeleccionados([]);
      setCedula("");
      setRespuesta(null);
    } catch (err) {
      alert("Error al generar factura: " + err.message);
    }
  };

  return (
    <div>
      <h2>Facturar</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Cédula del cliente:
          <input
            type="text"
            value={cedula}
            onChange={(e) => setCedula(e.target.value)}
            required
          />
        </label>
        <button type="submit">Validar antecedentes</button>
      </form>

      {respuesta === "sin antecedentes" && (
        <div>
          <h3>Selecciona productos para la factura</h3>
          <ul>
            {inventario.map((item) => (
              <li key={item._id}>
                {item.nombre} ({item.cantidad} disponibles)
                <button onClick={() => handleAgregarProducto(item)}>Agregar</button>
              </li>
            ))}
          </ul>

          {productosSeleccionados.length > 0 && (
            <div>
              <h4>Productos seleccionados:</h4>
              <ul>
                {productosSeleccionados.map((p) => (
                  <li key={p._id}>
                    {p.nombre} - Cantidad: {p.cantidad}
                  </li>
                ))}
              </ul>
              <button onClick={handleFacturar}>Generar factura</button>
            </div>
          )}
        </div>
      )}

      {respuesta && respuesta !== "sin antecedentes" && <p style={{ color: "red" }}>No se puede facturar: {respuesta}</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}
    </div>
  );
};

export default Facturar;
