import React, { useContext, useEffect, useState } from "react";
import { AuthContext } from "../context/AuthContext";
import { getUserById, getInventario } from "../services/api"; // ðŸ‘ˆ usaremos tu endpoint /api/user/:id
import Facturar from "./Facturar";
import SubirInventario from "./SubirInventario";
import Inventario from "./Inventario";
import "@fortawesome/fontawesome-free/css/all.min.css";

const Dashboard = () => {
  const { logout, token } = useContext(AuthContext);
  const [user, setUser] = useState(null);
  const [error, setError] = useState("");
  const [selectedPanel, setSelectedPanel] = useState("inicio");
  const [inventory, setInventory] = useState([]);

  useEffect(() => {
    const loadData = async () => {
      try {
        // Recupera los datos del usuario logueado desde localStorage
        const userFromLogin = JSON.parse(localStorage.getItem("user") || "null");

        if (!userFromLogin?._id) {
          setError("Usuario no encontrado en sesiÃ³n");
          return;
        }

        // Llama a tu API insegura GET /api/user/:id
        const userData = await getUserById(userFromLogin._id, token);
        setUser(userData);

        // Cargar inventario (mantienes tu lÃ³gica actual)
        const inventoryData = await getInventario(token);
        setInventory(inventoryData);
      } catch (err) {
        console.error(err);
        setError("No autorizado o error al cargar datos");
      }
    };

    if (token) loadData();
  }, [token]);

  const renderPanel = () => {
    switch (selectedPanel) {
      case "inicio":
        return (
          <div className="dashboard-card">
            <h3>Bienvenido, {user?.usuario}</h3>
            <p><strong>Email:</strong> {user?.email}</p>
            <p><strong>Rol:</strong> {user?._userInfo?.rango}</p>
            <p><strong>Creado:</strong> {new Date(user?.createdAt).toLocaleString()}</p>
          </div>
        );
      case "inventario":
        return <Inventario token={token} />;
      case "facturar":
        return <Facturar />;
      case "subirInventario":
        return (
          <SubirInventario
            token={token}
            onProductoAgregado={() => {
              getInventario(token).then(setInventory);
              setSelectedPanel("inventario");
            }}
          />
        );
      default:
        return null;
    }
  };

  if (error) return <p>{error}</p>;
  if (!user) return <p>Cargando...</p>;

  return (
    <div className="employee-dashboard">
      <aside className="sidebar">
        <h3>Panel Empleado</h3>
        <ul>
          <li onClick={() => setSelectedPanel("inicio")}>
            <i className="fas fa-home"></i> Inicio
          </li>
          <li onClick={() => setSelectedPanel("inventario")}>
            <i className="fas fa-boxes"></i> Inventario
          </li>
          <li onClick={() => setSelectedPanel("subirInventario")}>
            <i className="fas fa-upload"></i> Subir inventario
          </li>
          <li onClick={() => setSelectedPanel("facturar")}>
            <i className="fas fa-file-invoice-dollar"></i> Facturar
          </li>
          <li onClick={logout}>
            <i className="fas fa-sign-out-alt"></i> Cerrar sesiÃ³n
          </li>
        </ul>
      </aside>

      <main className="main-panel">
        <div className="main-header">
          <h2>
            {selectedPanel === "inicio" && "Inicio"}
            {selectedPanel === "inventario" && "Inventario"}
            {selectedPanel === "subirInventario" && "Subir Inventario"}
            {selectedPanel === "facturar" && "Facturar"}
          </h2>
          <button onClick={logout}>
            <i className="fas fa-sign-out-alt"></i> Salir
          </button>
        </div>

        {renderPanel()}
      </main>
    </div>
  );
};

export default Dashboard;
