import React, { useEffect, useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";
import {
  getInventario,
  createCompra,
  getCompras,
  deleteCompra,
} from "../services/api";

const ComprarProductos = () => {
  const { token } = useContext(AuthContext);
  const [inventory, setInventory] = useState([]);
  const [cart, setCart] = useState([]);
  const [compras, setCompras] = useState([]);
  const [error, setError] = useState("");

  // Cargar inventario
  useEffect(() => {
    const loadInventario = async () => {
      try {
        if (!token) return;
        const data = await getInventario(token);
        setInventory(data);
      } catch (err) {
        setError("Error al cargar inventario: " + err.message);
      }
    };
    loadInventario();
  }, [token]);

  // Cargar historial de compras del usuario
  useEffect(() => {
    const loadCompras = async () => {
      try {
        if (!token) return;
        const data = await getCompras(token); // token incluido
        setCompras(data);
      } catch (err) {
        console.error("Error al obtener compras:", err.message);
        setCompras([]); // evitar estado roto
      }
    };
    loadCompras();
  }, [token]);

  // Agregar productos al carrito
  const addToCart = (item) => {
    const exists = cart.find((p) => p._id === item._id);
    if (exists) {
      setCart(
        cart.map((p) =>
          p._id === item._id ? { ...p, cantidad: p.cantidad + 1 } : p
        )
      );
    } else {
      setCart([...cart, { ...item, cantidad: 1 }]);
    }
  };

  // Calcular total
  const total = cart.reduce((sum, p) => sum + p.precio * p.cantidad, 0);

  // Confirmar compra
  const handleCompra = async () => {
    try {
      const payload = {
        productos: cart.map((p) => ({
          productoId: p._id,
          nombre: p.nombre,
          precio: p.precio,
          cantidad: p.cantidad,
        })),
        total,
      };

      const result = await createCompra(token, payload);
      console.log("Compra registrada:", result);
      setCart([]);
      alert("Compra registrada correctamente (inseguro).");

      // Actualizar historial de compras del usuario
      setCompras((prev) => [result.compra, ...prev]);
    } catch (err) {
      alert("Error al crear la compra: " + err.message);
    }
  };

  // Eliminar compra
  const handleEliminarCompra = async (id) => {
    try {
      await deleteCompra(token, id); // token incluido
      setCompras((prev) => prev.filter((c) => c._id !== id));
      alert("Compra eliminada");
    } catch (err) {
      alert("Error al eliminar compra: " + err.message);
    }
  };

  if (error) return <p className="text-danger">{error}</p>;

  return (
    <div>
      <h2>Comprar Productos</h2>
      <div className="row">
        {inventory.map((item) => (
          <div key={item._id} className="col-md-4 mb-3">
            <div className="card p-3">
              <h5>{item.nombre}</h5>
              <p>Precio: ${item.precio}</p>
              <button
                className="btn btn-sm btn-success"
                onClick={() => addToCart(item)}
              >
                Agregar al carrito
              </button>
            </div>
          </div>
        ))}
      </div>

      {cart.length > 0 && (
        <div className="mt-4">
          <h4>ðŸ›’ Carrito</h4>
          <ul className="list-group">
            {cart.map((p) => (
              <li key={p._id} className="list-group-item">
                {p.nombre} x {p.cantidad} â€” ${p.precio * p.cantidad}
              </li>
            ))}
          </ul>
          <h5 className="mt-3">Total: ${total.toFixed(2)}</h5>
          <button className="btn btn-primary mt-2" onClick={handleCompra}>
            Confirmar compra
          </button>
        </div>
      )}

      <hr />
      <h2>Historial de Compras</h2>
      {compras.length === 0 ? (
        <p>No hay compras registradas.</p>
      ) : (
        <ul className="list-group">
          {compras.map((c) => (
            <li
              key={c._id}
              className="list-group-item d-flex justify-content-between align-items-center"
            >
              <div>
                <strong>{c.usuario}</strong> â€” ${c.total} â€”{" "}
                {new Date(c.fecha).toLocaleString()}
                <ul>
                  {c.productos.map((p) => (
                    <li key={p.productoId?._id || p.nombre}>
                      {p.nombre} x {p.cantidad} â€” ${p.precio * p.cantidad}
                    </li>
                  ))}
                </ul>
              </div>
              <button
                className="btn btn-sm btn-danger"
                onClick={() => handleEliminarCompra(c._id)}
              >
                Eliminar
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default ComprarProductos;
