import React, { useEffect, useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";
import { getInventario, createCompra } from "../services/api";

const ComprarProductos = () => {
  const { token } = useContext(AuthContext);
  const [inventory, setInventory] = useState([]);
  const [cart, setCart] = useState([]);
  const [error, setError] = useState("");
  const [mensaje, setMensaje] = useState("");

  // üîπ Cargar inventario al montar
  useEffect(() => {
    const loadInventario = async () => {
      try {
        const data = await getInventario(token);
        setInventory(data);
      } catch (err) {
        setError("Error al cargar inventario: " + err.message);
      }
    };
    if (token) loadInventario();
  }, [token]);

  // üîπ Agregar producto al carrito
  const addToCart = (item) => {
    const exists = cart.find((p) => p._id === item._id);
    if (exists) {
      setCart(
        cart.map((p) =>
          p._id === item._id ? { ...p, cantidad: p.cantidad + 1 } : p
        )
      );
    } else {
      setCart([...cart, { ...item, cantidad: 1 }]);
    }
  };

  // üîπ Calcular total
  const total = cart.reduce((sum, p) => sum + p.precio * p.cantidad, 0);

  // üîπ Enviar compra al backend (sin validaci√≥n real de token)
  const handleCompra = async () => {
    try {
      const payload = {
        productos: cart.map((p) => ({
          productoId: p._id,
          nombre: p.nombre,
          precio: p.precio,
          cantidad: p.cantidad,
        })),
        total,
      };

      // ‚ö†Ô∏è El backend acepta token pero NO lo valida (simulaci√≥n insegura)
      const result = await createCompra(token, payload);
      console.log("Compra registrada (insegura):", result);

      setCart([]);
      setMensaje("‚úÖ Compra registrada correctamente (sin validaci√≥n de token).");
    } catch (err) {
      alert("Error al crear la compra: " + err.message);
    }
  };

  if (error) return <p className="text-danger">{error}</p>;

  return (
    <div className="container mt-4">
      <h2 className="mb-4">üõçÔ∏è Comprar Productos</h2>

      <div className="row">
        {inventory.length === 0 ? (
          <p>No hay productos disponibles.</p>
        ) : (
          inventory.map((item) => (
            <div key={item._id} className="col-md-4 mb-3">
              <div className="card p-3 shadow-sm">
                <h5>{item.nombre}</h5>
                <p>Precio: ${item.precio}</p>
                <button
                  className="btn btn-sm btn-success"
                  onClick={() => addToCart(item)}
                >
                  Agregar al carrito
                </button>
              </div>
            </div>
          ))
        )}
      </div>

      {cart.length > 0 && (
        <div className="mt-4 p-3 border rounded bg-light">
          <h4>üõí Carrito</h4>
          <ul className="list-group">
            {cart.map((p) => (
              <li key={p._id} className="list-group-item d-flex justify-content-between">
                <span>
                  {p.nombre} x {p.cantidad}
                </span>
                <strong>${(p.precio * p.cantidad).toFixed(2)}</strong>
              </li>
            ))}
          </ul>
          <h5 className="mt-3">Total: ${total.toFixed(2)}</h5>
          <button className="btn btn-primary mt-2" onClick={handleCompra}>
            Confirmar compra
          </button>
        </div>
      )}

      {mensaje && <p className="text-success mt-3">{mensaje}</p>}
    </div>
  );
};

export default ComprarProductos;
