import React, { useState } from "react";
import { verificarAntecedentes, getInventario, facturarCliente } from "../services/api";

const Facturar = () => {
  const [cedula, setCedula] = useState("");
  const [estadoAntecedentes, setEstadoAntecedentes] = useState(null);
  const [error, setError] = useState("");
  const [inventario, setInventario] = useState([]);
  const [productosSeleccionados, setProductosSeleccionados] = useState([]);
  const [puedeFacturar, setPuedeFacturar] = useState(false);

  const token = localStorage.getItem("token");

  const handleVerificar = async (e) => {
    e.preventDefault();
    setError("");
    setProductosSeleccionados([]);
    setInventario([]);
    setEstadoAntecedentes(null);
    setPuedeFacturar(false);

    try {
      const data = await verificarAntecedentes(cedula, token);
      const estado = data.estado?.toLowerCase();

      setEstadoAntecedentes(estado);

      if (estado === "sin antecedentes") {
        const productos = await getInventario(token);
        setInventario(productos);
        setPuedeFacturar(true);
      }
    } catch (err) {
      setError(err.message || "Error al verificar antecedentes");
    }
  };

  const handleAgregarProducto = (producto) => {
    setProductosSeleccionados((prev) => {
      const existe = prev.find((p) => p._id === producto._id);
      if (existe) {
        if (existe.cantidad < producto.cantidad) {
          return prev.map((p) =>
            p._id === producto._id ? { ...p, cantidad: p.cantidad + 1 } : p
          );
        } else {
          alert("Stock insuficiente");
          return prev;
        }
      } else {
        return [...prev, { ...producto, cantidad: 1 }];
      }
    });
  };

  const handleFacturar = async () => {
    if (productosSeleccionados.length === 0) {
      alert("Selecciona al menos un producto");
      return;
    }

    try {
      const factura = {
        cedula,
        productos: productosSeleccionados.map(({ _id, cantidad }) => ({ id: _id, cantidad }))
      };

      const result = await facturarCliente(factura, token);
      alert("Factura generada con éxito");

      // Reiniciar todo
      setCedula("");
      setEstadoAntecedentes(null);
      setInventario([]);
      setProductosSeleccionados([]);
      setPuedeFacturar(false);
    } catch (err) {
      alert("Error al generar factura: " + err.message);
    }
  };

  return (
    <div>
      <h2>Facturar</h2>

      <form onSubmit={handleVerificar}>
        <label>
          Cédula del cliente:
          <input
            type="text"
            value={cedula}
            onChange={(e) => setCedula(e.target.value)}
            required
          />
        </label>
        <button type="submit">Verificar antecedentes</button>
      </form>

      {/* Mensaje del estado de antecedentes */}
      {estadoAntecedentes && (
        <p style={{ color: estadoAntecedentes === "sin antecedentes" ? "green" : "red" }}>
          Estado: {estadoAntecedentes}
        </p>
      )}

      {/* Selección de productos solo si puede facturar */}
      {puedeFacturar && (
        <div>
          <h3>Selecciona productos para la factura</h3>
          <ul>
            {inventario.map((item) => (
              <li key={item._id}>
                {item.nombre} ({item.cantidad} disponibles)
                <button onClick={() => handleAgregarProducto(item)}>Agregar</button>
              </li>
            ))}
          </ul>

          {productosSeleccionados.length > 0 && (
            <div>
              <h4>Productos seleccionados:</h4>
              <ul>
                {productosSeleccionados.map((p) => (
                  <li key={p._id}>
                    {p.nombre} - Cantidad: {p.cantidad}
                  </li>
                ))}
              </ul>
              <button onClick={handleFacturar}>Generar factura</button>
            </div>
          )}
        </div>
      )}

      {error && <p style={{ color: "red" }}>{error}</p>}
    </div>
  );
};

export default Facturar;
